document.addEventListener(
  "DOMContentLoaded",
  function () {
    var t = document.querySelectorAll('[data-bs-toggle="toast"]');
    for (var e of t)
      e.addEventListener("click", function () {
        var t = e.getAttribute("data-bs-target");
        if (t)
          try {
            var o = document.querySelector(t);
            if (!o) return;
            new bootstrap.Toast(o).show();
          } catch (t) {
            console.error(t);
          }
      });
  },
  !1
);
